<template>
  <div class="category">
    <tobar class="tobar">
      <div class="search_bar_middle flex-item">
        <i class="search_i"></i>
        <input type="search" placeholder="wlh" />
      </div>
    </tobar>

    <div class="wrap flex"> 
      <aside
        class="aside"
        ref="aside"
        @touchstart="ontouchstart"
        @touchmove="ontouchmove"
        @touchend="ontouchend"
        @touchcancel="ontouchcancel" 
      >
        <ul :style="{top:tmp+'px'}" :class="{transition:transition}" ref="scroll">
          <li
            v-for="(item,index) in 100"
            :class="{active:navIndex==index}"
            @click="onClick(index)"
            :key="index"
          >分类：{{item}}</li>
        </ul>
      </aside>

      <!-- content -->
      <div class="content">
          <content1></content1>

       
      </div>
    </div>

    <navbar></navbar>
  </div>
</template>
    
<script>
import navbar from "@/components/Navbar";
import tobar from "@/components/Tobar";
import content1 from "@/components/Content";


export default {
  components: {
    navbar,
    tobar,
    content1
  },
  data() {
    return {
      navIndex: 0,
      tmp: 0,
      offsetTop: 0,
      limitTop: 0,
      startY: 0,
      transition: false,
     
    };
  },
  mounted() {
    // 计算滑动到底部的限定值
    this.limitTop =
      this.$refs["aside"].offsetHeight -
      this.$refs["scroll"].offsetHeight -
      150;
      console.log(this.limitTop+"this.limitTop")

  },
  methods: {
    ontouchstart(e) {
      //获取初始位置
      this.startY = e.changedTouches[0].clientY;
      // scroll.className='';
      this.transition = false;
    
    },
    ontouchmove(e) {
      var y = e.changedTouches[0].clientY;
      // 计算滑动的距离 (this.offsetTop:自身原始的y坐标)
      this.tmp = this.offsetTop + y - this.startY;

      this.tmp = this.tmp > 150 ? 150 : this.tmp;

      this.tmp = this.tmp < this.limitTop ? this.limitTop : this.tmp;
     
    },
    ontouchend(e) {
      console.log(2);
  
      this.tmp = this.tmp > 0 ? 0 : this.tmp;
      this.tmp =
        this.tmp < this.limitTop + 150 ? this.limitTop + 150 : this.tmp;
      this.offsetTop = this.tmp;
      this.transition = true;
    },
    ontouchcancel(e) {
      // 返回顶部
      this.tmp = this.tmp > 0 ? 0 : this.tmp;
      this.tmp =
        this.tmp < this.limitTop + 150 ? this.limitTop + 150 : this.tmp;
      this.offsetTop = this.tmp;
      this.transition = true;
    },
    onClick(index) {
      this.navIndex = index;
      console.log(this.navIndex);
      // 根据 索引值计算滚动的距离
      this.tmp = this.$refs["scroll"].offsetHeight * (this.navIndex / 100) * -1;

      // 越界处理
      this.tmp = this.tmp > 0 ? 0 : this.tmp;
      this.tmp =
        this.tmp < this.limitTop + 150 ? this.limitTop + 150 : this.tmp;
      this.offsetTop = this.tmp;
      this.transition = true;
    }
  }
};
</script>
